  var roomPicObj;
  var surPicObj;
  
  function roomPicSlide(picArrayN,parDivId,dotIdFro,picIdFro){
	  var interObj=null;//定时器对象
	  var slidingObj=new slidingPicture();//滚屏对象
	  var picArray=picArrayN;//图片数组
  	  var picN=-1;//当前图片序号
  	  var dotId=dotIdFro;//圆点图片id前缀
  	  var picId=picIdFro;//图片id前缀
  	  var that=this;
	  this.loadRoomPic=function(add,bz,interFlag){
		if(interFlag=="cls" || picArray.length<2){
			try{
				clearInterval(interObj);
			}catch(e){}
		}
	    if(picArray.length<1 || slidingObj.slideFlag){
	  		return;
	  	}
	    var oldN=picN;
	    picN=addNumForSlide(picN,add,bz,picArray.length);
	  	if(oldN==picN){
	  		return;
	  	}
	  	if(oldN>-1){
	  		document.getElementById(dotId+oldN).src="public/desktop/images/yuan15_ls.png";
	  	}
	  	document.getElementById(dotId+picN).src="public/desktop/images/yuan15_hs.png";
	  	if(slidingObj.imgIdbk){
	  		document.getElementById(slidingObj.imgIdbk).src=picArray[picN];
	  		if(bz==0){//点击
	  			slidingObj.slide(oldN-picN,30,1);
	  		}else{//左右移动
	  			slidingObj.slide(-add,30,1);
	  		}
	  	}else{
	  		document.getElementById(picId).src=picArray[picN];
	  	}
	  }
	  //加载默认图片
	  that.loadRoomPic(0);
	  //加载滑屏事件
	  interObj=slidingObj.bindEvent(parDivId, function(add,bz){that.loadRoomPic(add,bz);} ,picId,3000);
	  //interObj=bindEvent("surroundPicDiv",loadRoomPic);//图片直接替换模式
  }
  
